import { Component } from '@angular/core';
import { RouterOutlet } from '@angular/router';
import { CommonModule } from '@angular/common';
import { MatToolbarModule } from '@angular/material/toolbar';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatSidenavModule } from '@angular/material/sidenav';
import { MatListModule } from '@angular/material/list';

/**
 * Componente principal de la aplicaci√≥n Angular.
 * 
 * Este es el componente ra√≠z que contiene:
 * - La barra de navegaci√≥n superior
 * - El men√∫ lateral (sidenav)
 * - El √°rea principal donde se cargan los componentes de las rutas
 * 
 * En Angular 17+, los componentes son standalone por defecto,
 * lo que significa que no necesitan estar declarados en un m√≥dulo.
 */
@Component({
  selector: 'app-root',
  standalone: true,
  imports: [
    CommonModule,
    RouterOutlet,
    MatToolbarModule,
    MatButtonModule,
    MatIconModule,
    MatSidenavModule,
    MatListModule
  ],
  template: `
    <mat-toolbar color="primary">
      <button mat-icon-button (click)="sidenav.toggle()">
        <mat-icon>menu</mat-icon>
      </button>
      <span>üè¶ Gesti√≥n de Cuentas de Ahorros</span>
      <span class="toolbar-spacer"></span>
      <button mat-icon-button>
        <mat-icon>account_circle</mat-icon>
      </button>
    </mat-toolbar>

    <mat-sidenav-container class="sidenav-container">
      <mat-sidenav #sidenav mode="side" opened class="sidenav">
        <mat-nav-list>
          <a mat-list-item routerLink="/dashboard" routerLinkActive="active">
            <mat-icon>dashboard</mat-icon>
            <span>Dashboard</span>
          </a>
          <a mat-list-item routerLink="/cuentas" routerLinkActive="active">
            <mat-icon>account_balance</mat-icon>
            <span>Cuentas</span>
          </a>
          <a mat-list-item routerLink="/transacciones" routerLinkActive="active">
            <mat-icon>swap_horiz</mat-icon>
            <span>Transacciones</span>
          </a>
        </mat-nav-list>
      </mat-sidenav>

      <mat-sidenav-content class="sidenav-content">
        <div class="content-container">
          <router-outlet></router-outlet>
        </div>
      </mat-sidenav-content>
    </mat-sidenav-container>
  `,
  styles: [`
    .toolbar-spacer {
      flex: 1 1 auto;
    }

    .sidenav-container {
      height: calc(100vh - 64px);
    }

    .sidenav {
      width: 250px;
    }

    .sidenav-content {
      padding: 20px;
    }

    .content-container {
      max-width: 1200px;
      margin: 0 auto;
    }

    mat-nav-list a {
      display: flex;
      align-items: center;
      gap: 12px;
    }

    mat-nav-list a.active {
      background-color: rgba(63, 81, 181, 0.1);
    }

    mat-nav-list mat-icon {
      margin-right: 8px;
    }
  `]
})
export class AppComponent {
  /**
   * T√≠tulo de la aplicaci√≥n.
   */
  title = 'Gesti√≥n de Cuentas de Ahorros';

  /**
   * Referencia al sidenav para poder controlarlo desde el template.
   */
  sidenav: any;
}
